async function toggleAuth() { // Login/Logout action
    const user = auth.currentUser;
    if(user) {
        await auth.signOut();
        alert("Logged out successfully.");
    } else {
        try {
            await auth.signInWithPopup(provider);
        } catch(e) {
            console.log("Login failed:", e);
        }
    }
}

async function getSecureHeaders() {
    let token = "";
    if (auth.currentUser) {
        token = await auth.currentUser.getIdToken();
    }
    return { 
        'Content-Type': 'application/json',
        'X-App-Secret': _S,
        'Authorization': `Bearer ${token}`
    };
}

let currentMetadata = {};

async function startDownload() {
    const url = document.getElementById('urlInput').value.trim();
    const btn = document.getElementById('downloadBtn');
    const progressUI = document.getElementById('progressUI');
    const statusText = document.getElementById('statusText');
    const progressBar = document.getElementById('progressBar');
    const resultDiv = document.getElementById('result');

    if (!url) {
        alert('Please paste a link first!');
        return;
    }

    // Reset and Show Progress
    btn.disabled = true;
    btn.innerHTML = ' Initializing...';
    resultDiv.style.display = 'none';
    progressUI.style.display = 'flex';
    progressBar.style.width = '10%';
    statusText.innerText = 'Connecting to server...';

    // 1. Fetch Instant Preview (Wow Factor)
    try {
        const hdrs = await getSecureHeaders();
        const prevRes = await fetch(`${API_BASE}/preview`, {
            method: 'POST',
            headers: hdrs,
            body: JSON.stringify({ url })
        });
        if (prevRes.ok) {
            const prevData = await prevRes.json();
            if (prevData && prevData.success) {
                currentMetadata = prevData;
                document.getElementById('progThumb').src = prevData.thumbnail;
                document.getElementById('progTitle').innerText = prevData.title;
            }
        }
    } catch (e) {
        console.log("Preview failed:", e);
    }

    // 2. Start Actual Download
    try {
        const hdrs = await getSecureHeaders();
        const response = await fetch(`${API_BASE}/download`, {
            method: 'POST',
            headers: hdrs,
            body: JSON.stringify({ url: url })
        });
        const data = await response.json();

        if (data.success) {
            if (data.status === 'ready') {
                showSuccess(data);
                // Confetti burst on success! üéâ
                confetti({
                    particleCount: 150,
                    spread: 70,
                    origin: { y: 0.6 },
                    colors: ['#3b82f6', '#2dd4bf', '#f09433', '#e6683c']
                });
            } else if (data.status === 'pending') {
                statusText.innerText = 'Server blocked. Activating Smart Backup...';
                progressBar.style.width = '50%';
                pollStatus(data.job_id);
            }
        } else {
            showError(data.message || 'Error occurred');
        }
    } catch (e) {
        showError('Connection Error. Check console.');
        console.error("Download Error:", e);
    }
}

async function pollStatus(jobId) {
    const statusText = document.getElementById('statusText');
    const progressBar = document.getElementById('progressBar');
    let count = 0;

    const interval = setInterval(async () => {
        count++;
        if (count > 60) { clearInterval(interval); showError('Process Timeout.'); return; }

        try {
            const res = await fetch(`${API_BASE}/status/${jobId}`);
            const data = await res.json();

            if (data.status === 'ready') {
                clearInterval(interval);
                showSuccess({
                    filename: data.filename,
                    title: currentMetadata.title || "Instagram Video",
                    thumbnail: currentMetadata.thumbnail || "https://icons.iconarchive.com/icons/paomedia/small-n-flat/512/social-instagram-icon.png"
                });
            } else {
                statusText.innerText = `Video Processing... (${count * 5}s)`;
                progressBar.style.width = `${50 + (count * 0.7)}%`;
            }
        } catch (e) {}
    }, 5000);
}

function showSuccess(data) {
    document.getElementById('progressUI').style.display = 'none';
    const res = document.getElementById('result');
    res.style.display = 'block';

    document.getElementById('resTitle').innerText = data.title || "Instagram Video";
    document.getElementById('resThumb').src = data.thumbnail || "https://icons.iconarchive.com/icons/paomedia/small-n-flat/512/social-instagram-icon.png";
    document.getElementById('downloadLink').href = `${API_BASE}/files/${data.filename}`;

    // Refresh limits
    if(typeof updateLimits === 'function') updateLimits();

    resetBtn();
}

function copyLink() {
    const url = document.getElementById('urlInput').value;
    navigator.clipboard.writeText(url).then(() => {
        alert('Link copied to clipboard!');
    });
}

function showError(msg) {
    document.getElementById('statusText').innerText = msg;
    document.getElementById('statusText').style.color = '#ef4444';
    resetBtn();
}

function resetBtn() {
    const btn = document.getElementById('downloadBtn');
    btn.disabled = false;
    btn.innerText = 'Download Now';
}

