// Limit & Points & referral Logic
let referralID = "";

// Capture 'ref' from URL
const urlParams = new URLSearchParams(window.location.search);
const refParam = urlParams.get('ref') || "";

async function updateCredits() {
    try {
        const hdrs = await getSecureHeaders();
        const res = await fetch(`${API_BASE}/check-limit`, {
            method: 'POST',
            headers: hdrs,
            body: JSON.stringify({ ref: refParam })
        });
        const data = await res.json();

        if (!data.success === false && data.message && data.message.includes('Unauthorized')) {
            // User not logged in, silented fallback handled by UI display
            return;
        }

        const credits = data.credits || 0;
        const balance = data.balance || 0;
        referralID = data.referral_id || "";

        // limitText does not exist anymore, removing to prevent console errors
        const navCre = document.getElementById('navCredits');
        if (navCre) navCre.innerText = credits;
        const navBal = document.getElementById('navBalance');
        if (navBal) navBal.innerText = balance.toFixed(2);
        const dashBal = document.getElementById('dashBalance');
        if (dashBal) dashBal.innerText = balance.toFixed(2);
        const refInp = document.getElementById('refUrl');
        if (refInp && referralID) refInp.value = `https://argha-7.blogspot.com?ref=${referralID}`;

        const wd = document.getElementById('walletDisplay');
        if (wd) {
            if (credits < 10) {
                wd.style.background = 'rgba(239, 68, 68, 0.1)';
            } else {
                wd.style.background = 'rgba(59, 130, 246, 0.1)';
            }
        }
    } catch (e) {
        console.error("Credit fetch failed");
    }
}

