async function withdraw() {
    const upi = document.getElementById('upiInput').value;
    if (!upi.includes('@')) { alert('Please enter valid UPI ID'); return; }

    try {
        const hdrs = await getSecureHeaders();
        const res = await fetch(`${API_BASE}/withdraw`, {
            method: 'POST',
            headers: hdrs,
            body: JSON.stringify({ upi_id: upi })
        });
        const data = await res.json();
        alert(data.message);
    } catch (e) {
        alert('Withdrawal failed. Try again.');
    }
}

async function claimReward() {
    // Short delay to ensure they click the link
    setTimeout(async () => {
        if (!auth.currentUser) return; // Must be logged in to earn
        try {
            const hdrs = await getSecureHeaders();
            const res = await fetch(`${API_BASE}/reward-share`, {
                method: 'POST',
                headers: hdrs,
                body: JSON.stringify({})
            });
            const data = await res.json();
            if (data.success) {
                updateCredits();
                if(typeof confetti !== "undefined") {
                    confetti({ particleCount: 150, spread: 70, origin: { y: 0.6 } });
                }
                alert(data.message);
            }
        } catch (e) {
            console.error("Reward failed");
        }
    }, 2000);
}


