// --- INTERACTIVE FEATURES: TYPING & PARTICLES ---

// 1. Typing Effect Logic
const typingText = document.getElementById('typing-text');
const phrases = ["Reels", "Stories", "IGTV", "Videos", "Photos"];
let phraseIdx = 0;
let charIdx = 0;
let isDeleting = false;

function typeEffect() {
    const currentPhrase = phrases[phraseIdx];
    if (isDeleting) {
        typingText.innerText = currentPhrase.substring(0, charIdx - 1);
        charIdx--;
    } else {
        typingText.innerText = currentPhrase.substring(0, charIdx + 1);
        charIdx++;
    }

    let speed = isDeleting ? 50 : 150;
    if (!isDeleting && charIdx === currentPhrase.length) {
        isDeleting = true;
        speed = 2000; // Pause at end
    } else if (isDeleting && charIdx === 0) {
        isDeleting = false;
        phraseIdx = (phraseIdx + 1) % phrases.length;
        speed = 500;
    }
    setTimeout(typeEffect, speed);
}
typeEffect();

// 2. Antigravity Halo Particle System
const canvas = document.getElementById('particle-canvas');
const ctx = canvas.getContext('2d');
let particles = [];
// Signature Antigravity Galaxy Palette
const colors = ['#3b82f6', '#2dd4bf', '#f09433', '#cc2366', '#dc2743'];

function resize() {
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;
}
window.addEventListener('resize', resize);
resize();

class Particle {
    constructor() {
        this.init();
    }
    init() {
        // Full screen distribution with central weight
        this.x = Math.random() * canvas.width;
        this.y = Math.random() * canvas.height;

        // Base position for return logic (centered-weight random)
        this.baseX = this.x;
        this.baseY = this.y;

        this.size = Math.random() * 2 + 0.3; 
        this.color = colors[Math.floor(Math.random() * colors.length)];
        this.density = (Math.random() * 30) + 15;
        this.alpha = Math.random() * 0.4 + 0.1;

        this.vX = (Math.random() - 0.5) * 0.8;
        this.vY = (Math.random() - 0.5) * 0.8;

        // Transformation Properties
        this.stretch = 1;
        this.angle = 0;
        this.isInner = false;
    }
    update() {
        // 1. Constant Jitter & Drift
        this.baseX += this.vX;
        this.baseY += this.vY;
        if (this.baseX < 0) this.baseX = canvas.width;
        if (this.baseX > canvas.width) this.baseX = 0;
        if (this.baseY < 0) this.baseY = canvas.height;
        if (this.baseY > canvas.height) this.baseY = 0;

        // 2. Interaction & Stretch Logic
        let dx = mouseX - this.x;
        let dy = mouseY - this.y;
        let distance = Math.sqrt(dx*dx + dy*dy);
        let maxDistance = 200;

        // Color Shift Logic (Inner = Black)
        this.isInner = distance < 80;

        if (distance < maxDistance) {
            let force = (maxDistance - distance) / maxDistance;
            let directionX = (dx / distance) * force * this.density;
            let directionY = (dy / distance) * force * this.density;

            this.x -= directionX;
            this.y -= directionY;

            // Stretch & Compress Logic
            this.angle = Math.atan2(dy, dx);
            this.stretch = 1 + (force * 2.5); // Elongate based on force
        } else {
            this.x -= (this.x - this.baseX) / 10;
            this.y -= (this.y - this.baseY) / 10;
            this.stretch = 1; // Return to circle
        }
    }
    draw() {
        ctx.save();
        ctx.translate(this.x, this.y);
        ctx.rotate(this.angle);

        // Draw Color based on distance
        ctx.fillStyle = this.isInner ? "#000000" : this.color;
        ctx.globalAlpha = this.isInner ? 0.9 : this.alpha;

        ctx.beginPath();
        // Stretch along X axis
        ctx.ellipse(0, 0, this.size * this.stretch, this.size / Math.sqrt(this.stretch), 0, 0, Math.PI * 2);
        ctx.fill();

        ctx.restore();
        ctx.globalAlpha = 1;
    }
}

function initParticles() {
    particles = [];
    // Ultra-dense particle field (1 dot per 800 pixels)
    const count = (canvas.width * canvas.height) / 800;
    for (let i = 0; i < count; i++) {
        particles.push(new Particle());
    }
}
initParticles();
window.addEventListener('resize', initParticles);

function animate() {
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    for (let i = 0; i < particles.length; i++) {
        particles[i].update();
        particles[i].draw();
    }
    requestAnimationFrame(animate);
}
animate();

// 3. Aura Cursor Easing Logic
const point = document.getElementById('cursor-point');
const aura = document.getElementById('cursor-aura');
document.addEventListener('mousemove', (e) => {
    mouseX = e.clientX;
    mouseY = e.clientY;
    if(point) point.style.transform = `translate(${mouseX}px, ${mouseY}px)`;
});

function updateAura() {
    // Smooth easing (lerp)
    auraX += (mouseX - auraX) * 0.15;
    auraY += (mouseY - auraY) * 0.15;
    aura.style.left = auraX + 'px';
    aura.style.top = auraY + 'px';
    requestAnimationFrame(updateAura);
}
updateAura();

// Hover interactions
const interactives = document.querySelectorAll('button, a, input, .f-card, .p-card');
interactives.forEach(el => {
    el.addEventListener('mouseenter', () => aura.classList.add('hover'));
    el.addEventListener('mouseleave', () => aura.classList.remove('hover'));
});
